import React,{Component} from 'react';
import { Row, Col, Divider,Layout ,Typography,Button, Menu} from 'antd';
import { browserHistory } from 'react-router';
import Router, { withRouter } from 'next/router'
import { SearchOutlined, ReadOutlined, FormOutlined  ,QuestionCircleOutlined} from '@ant-design/icons';
import Link from 'next/link';
const { SubMenu } = Menu;
class SideMenu extends Component{
    state={
        current: '/'
    }
    handleClick=(e)=>{
        console.log("link clicked... ", this.props.pathname)
        Router.push(e.key);
        this.setState({ current: e.key });
    }
    render(){

        return(
            <div className="side-menu">
                    <Menu
                        onClick={this.handleClick}
                        // selectedKeys={[this.props.pathname]}  
                        style={{ width: 256 }}
                        defaultSelectedKeys={[this.state.current]}
                        //defaultOpenKeys={['sub1']}
                        mode="inline"
                        >
                        <Menu.Item key="/query"><FormOutlined />Ask Question</Menu.Item>
                        <Menu.Item key="/"><ReadOutlined/>Blogs</Menu.Item>
                        <Menu.Item key="/question"><SearchOutlined/>Question & Answer</Menu.Item>
                        <Menu.Item key="/jobs"><QuestionCircleOutlined />Jobs</Menu.Item>
                    </Menu>
                </div> 
        )
    }
}

export default (withRouter(SideMenu))
