import React, { Component } from "react";
import { Breadcrumb, Icon, Row, Col, Layout, Card, Carousel, Input, Button, Divider } from "antd";
import { fetchProfiles } from "../../redux/actions/dashboardAction";
import { connect } from "react-redux";
import SideMenuLeft from "../Common/SideMenuLeft";
import PreviewModel from "../Common/PreviewModal";
import PostCard from "../Common/PostCard";
// import "../../static/css/style.scss";
class Dashboard extends Component {
  state = {
    isModal: false,
    list: [
      {
        title: "First post",
        description:
          "It is a good platform to learn programming,It is an educational website. Prepare for the Recruitment drive of product",
        likes: "12",
        comment: "75",
      },
      {
        title: "First post",
        description:
          "It is a good platform to learn programming,It is an educational website. Prepare for the Recruitment drive of product",
        likes: "12",
        comment: "75",
      },
      {
        title: "First post",
        description:
          "It is a good platform to learn programming,It is an educational website. Prepare for the Recruitment drive of product",
        likes: "12",
        comment: "75",
      },
    ],
  };

  componentDidMount() {
    this.props.fetchProfiles();
  }
  showModal = () => {
    console.log("works");
    this.setState({ isModal: true });
  };
  closeModal = () => {
    this.setState({ isModal: false });
  };

  render() {
    const { users } = this.props;
    const { isModal } = this.state;
    return (
      <Layout className={isModal ? "dark-mode" : "normal-mode"}>
        <Row>
          <Col className="side-menu-left" span={4}>
            <SideMenuLeft showModal={this.showModal} />
          </Col>
          <Col span={10}>
            <Row className="post-list">
              <div>
                {this.state.list
                  ? this.state.list.map((i, j) => {
                      return (
                        <div>
                          <PostCard />
                        </div>
                      );
                    })
                  : null}
              </div>
            </Row>
          </Col>
          <Col span={10}>col</Col>
        </Row>
        {isModal ? <PreviewModel closeModal={this.closeModal} /> : ""}
      </Layout>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    users: state.profiles,
  };
};
function mapDispatchToProps(dispatch) {
  return {
    fetchProfiles: (data) => {
      dispatch(fetchProfiles(data));
    },
  };
}
export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
