import distanceInWordsToNow from "date-fns/distance_in_words_to_now";
import Link from "next/link";
import PostCard from '../Common/PostCard';
import CardHeader from '../Common/CardHeader';
import CardContent from '../Common/CardContent';
import CardFooter from '../Common/CardFooter';
import CardComment from '../Common/CardComment';
class Post extends React.PureComponent {
  state = {
    isLiked: true,
    numLikes: 0,
    comments: []
  };

  componentDidMount() {
    this.setState({
      isLiked: this.checkLiked(this.props.post.likes),
      numLikes: this.props.post.likes.length,
      comments: this.props.post.comments
    });
  }

  componentDidUpdate(prevProps) {
    if (prevProps.post.likes.length !== this.props.post.likes.length) {
      this.setState({
        isLiked: this.checkLiked(this.props.post.likes),
        numLikes: this.props.post.likes.length
      });
    }

    if (prevProps.post.comments.length !== this.props.post.comments.length) {
      this.setState({
        comments: this.props.post.comments
      });
    }
  }

  checkLiked = likes => likes.includes(this.props.auth.user._id);

  formatTimeCreated = time =>
    distanceInWordsToNow(time, {
      includeSeconds: true,
      addSuffix: true
    });

  render() {
    const {
      classes,
      post,
      auth,
      isDeletingPost,
      handleDeletePost,
      handleToggleLike,
      handleAddComment,
      handleDeleteComment
    } = this.props;
    const { isLiked, numLikes, comments } = this.state;
    console.log("your comments.......",comments)
    const isPostCreator = post.postedBy._id === auth.user._id;
    return (
          <PostCard>
            <CardHeader
              time={this.formatTimeCreated(post.createdAt)}
              name={post.postedBy.name}
              id={post.postedBy._id}
              avatar={post.postedBy.avatar}
            />
            <CardContent
                text={post.text}
                image={post.image}
            />
          <CardFooter
            commentCount={comments.length}
            totalLikes={numLikes}
            isLiked={isLiked}
            post={post}
            // handleToggleLike={handleToggleLike(post)}
          />
          <CardComment
            auth={auth}
            postId={post._id}
            comments={comments}
            handleAddComment={handleAddComment}
            // handleDeleteComment={handleDeleteComment}
          />
          </PostCard>

    );
  }
}



export default Post;
