import React, { Component } from "react";
import Router from "next/router";
import { Card ,Form, InputNumber, Button} from 'antd';
import { signinUser } from "../lib/auth";
import Layout from "../components/Layout/Layout";
import Input from '../../exampl/components/Common/Input'
class SignIn extends React.Component {
  state = {
    email: "",
    password: "",
    error: "",
    openError: false,
    isLoading: false
  };

  handleClose = () => this.setState({ openError: false });

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = event => {
    const { email, password } = this.state;
    console.log("success....")
    event.preventDefault();
    const user = { email, password };
    this.setState({ isLoading: true, error: "" });
    signinUser(user)
      .then(() => {
        console.log("success....")
        // Router.push("/");
      })
      .catch(this.showError);
  };

  showError = err => {
    const error = (err.response && err.response.data) || err.message;
    this.setState({ error, openError: true, isLoading: false });
  };
  openNotification = placement => {
    notification.info({
      message: `Notification ${placement}`,
      description:
        'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
      placement,
    });
  };
  render() {
    const { classes } = this.props;
    const { error, openError, isLoading } = this.state;

    return (
      <div className="signin-root">
        <Card style={{ width: 500 }}>
          <form onSubmit={this.handleSubmit} className="signin-form">
                <Input placeholder="Email" type="email" inputCss="email" onChange={this.handleChange} /><br/><br/>
                <Input placeholder="Password" type="password" inputCss="email"  onChange={this.handleChange}/><br/>
                <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                disabled={isLoading}
                className="sign-btn"
              >{isLoading ? "Signing in..." : "Sign in"}</Button>
            </form>
        </Card>
        {error && (
            this.openNotification("bottomRight")
          )}
      </div>
    );
  }
}

const styles = theme => ({
  root: {
    width: "auto",
    display: "block",
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up("md")]: {
      width: 400,
      marginLeft: "auto",
      marginRight: "auto"
    }
  },
  paper: {
    marginTop: theme.spacing.unit * 8,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: theme.spacing.unit * 2
  },
  avatar: {
    margin: theme.spacing.unit,
    backgroundColor: theme.palette.secondary.main
  },
  form: {
    width: "100%",
    marginTop: theme.spacing.unit
  },
  submit: {
    marginTop: theme.spacing.unit * 2
  },
  snack: {
    color: theme.palette.secondary.light
  }
});

export default SignIn;
